import csvfrom datetime import datetimedef save_deposit_to_csv(amount, distance, dilated_time):    timestamp=datetime.now().strftime("%Y-%m-%d %H:%M:%S")        depositdata=[timestamp,amount,distance,dilated_time]        with open("deposit.csv", "a", newline="") as file:        writer=csv.writer(file)                file.seek(0,2)                if file.tell()==0:            writer.writerow(["Timestamp", "Deposit Amount", "Distance(m)", "Dilated Time"])                                writer.writerow(depositdata)                                        def read_deposits_from_csv():        with open("deposit.csv", "r") as file:        reader=csv.reader(file)                next(reader)                        for row in reader:            print(row)def save_withdraw_to_csv(amount, distance, dilated_time):    timestamp=datetime.now().strftime("%Y-%m-%d %H:%M:%S")        withdrawaldata=[timestamp,amount,distance,dilated_time]        with open("withdrawal.csv", "a", newline="") as file:        writer=csv.writer(file)                file.seek(0,2)                if file.tell()==0:            writer.writerow(["Timestamp", "Withdrawal Amount", "Distance(m)", "Dilated Time"])                                writer.writerow(withdrawaldata)                                        def read_withdrawals_from_csv():        with open("withdrawal.csv", "r") as file:        reader=csv.reader(file)                next(reader)                        for row in reader:            print(row)            def sum_deposits():    total_deposit=0        with open("deposit.csv", "r") as file:        reader=csv.reader(file)        next(reader)        for row in reader:            total_deposit+=float(row[1])                return total_depositdef sum_withdrawals():    total_withdrawal=0        with open("withdrawal.csv", "r") as file:        reader=csv.reader(file)        next(reader)        for row in reader:            total_withdrawal+=float(row[1])                return total_withdrawal                        save_deposit_to_csv(100, 500000, 98.50363922737031)read_deposits_from_csv()save_withdraw_to_csv(100, 500000, 98.50363922737031)read_withdrawals_from_csv()